import {Routing} from "../Routing";
import {MakeUserQuerySystem} from "application/system/user/UserQuerySystem";
import logger from "framework/Logger";

export default class AuthRouting implements Routing {

    public handlers() {
        return {
            login: (req: any) => {
                return MakeUserQuerySystem().login(req.body);
            }
        };
    }

    public fillRoutes(router: any, handlers: any) {
        router.post("/login", handlers.login);
    }
}
