import {Routing} from "../../Routing";
import { Authorization } from "application/system/Authorization";

export default class BankRouting implements Routing {

    public handlers() {
        return {
            me: (req: any) => {
                return req.user;
            }
        };
    }

    public fillRoutes(router: any, handlers: any) {
        router.get('/me', handlers.me);
    }
}
