const faker = require('faker');
const factory = require('factory-girl').factory;
import {UserImpl} from "domain/user/User";

factory.define('User', UserImpl, {
    id: () => 'U' + faker.random.number(),
    type: 'user',
    username: () => faker.internet.userName(),
    password: () => faker.internet.password(),
    avatar: () => faker.internet.avatar(),
    wallets: {},
    info: {},
    phone: () => faker.phone.phoneNumber(),
    phone_verified: true,
    phone_verification_code: () => faker.random.number(),
    email: () => faker.internet.email(),
    email_verified: true,
    email_verification_code: () => faker.random.number(),
    createdAt: () => new Date(),
    updatedAt: () => new Date()
});

export const generateUser = factory.build.bind(factory, ('User'));
